import React, { Component, useRef, useEffect } from 'react';
import { render } from 'react-dom';
import './Menu.css';
import SlideToggle from 'react-slide-toggle';

const scrollToRef = (ref) => window.scrollTo(0, ref.current.screenTop);
const useMountEffect = (fun) => useEffect(fun, []);

const Menu = () => {

	const myRef = useRef(null);
	useMountEffect(() => scrollToRef(myRef)); // Scroll on mount

	return (
	<SlideToggle
		duration={800}
		collapsed
		render={({toggle, setCollapsibleElement, progress}, myRef) => (
			<div className="menu">
				<div className="slide-toggle__header">
					<div className="slide-toggle__button" onClick={()=>scrollToRef(myRef)} >
						<div className={'line'} />
						<div className={'line'} />
						<div className={'line'} />
					</div>
				</div>
				<div className="slide-toggle__box" ref={setCollapsibleElement}>
					<div className="slide-toggle__box-inner" style={{ opacity: Math.max(0.5, progress) }}>
						<div className={'menu-items'} ref={'menu-items'}>
							<a className={'menu-item'} ref={myRef}>About</a>
							<a className={'menu-item'} ref={myRef}>Skills</a>
							<a className={'menu-item'} ref={myRef}>Portfolio</a>
							<a className={'menu-item'} ref={myRef}>Contact</a>
						</div>
					</div>
				</div>
			</div>
		)}
	/>
	)
};

export default Menu;
