import React, {Component} from 'react';
import './Menu.css';
import SlideToggle from 'react-slide-toggle';
import { Link, animateScroll as scroll } from "react-scroll";

class Menu extends Component {

    constructor(props) {
        super(props);
        this.addActiveClass= this.addActiveClass.bind(this);
        this.state = {
            active: false,
        };
    }

    toggleClass = () => {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
    };

    scrollToTop = () => {
        scroll.scrollToTop();
    };

    render() {
        return (
            <nav className="nav" id="navbar">
                <div className="menu">
                    <div className="desktop-menu">
                        <div className={'logo'} onClick={this.scrollToTop}><span>Nebojsa Mitrovic</span></div>
                        <SlideToggle
                            duration={800}
                            collapsed
                            render={({toggle, setCollapsibleElement}) => (
                            <div className={'mobile-menu'}>
                                <div className="slide-toggle__header">
                                    <div className="slide-toggle__button" onClick={toggle}>
                                        <div className={'line top-line'} onClick/>
                                        <div className={'line center-line'} onClick/>
                                        <div className={'line bottom-line'} onClick/>
                                    </div>
                                </div>
                                <div className="slide-toggle__box" ref={setCollapsibleElement}>
                                    <div className="slide-toggle__box-inner">
                                        <div className={'menu-items mobile-menu-items'}>
                                            <Link
                                                activeClass="active"
                                                to="about"
                                                spy={true}
                                                smooth={true}
                                                offset={-50}
                                                duration={500}
                                                onClick={toggle}
                                            >
                                                <span className={'menu-item'}>About Me</span>
                                            </Link>
                                            <Link
                                                activeClass="active"
                                                to="skills"
                                                spy={true}
                                                smooth={true}
                                                offset={-50}
                                                duration={500}
                                                onClick={toggle}
                                            >
                                                <span className={'menu-item'}>Skills</span>
                                            </Link>
                                            <Link
                                                activeClass="active"
                                                to="portfolio"
                                                spy={true}
                                                smooth={true}
                                                offset={-50}
                                                duration={500}
                                                onClick={toggle}
                                            >
                                                <span className={'menu-item'}>Portfolio</span>
                                            </Link>
                                            <Link activeClass="active"
                                                  to="contact"
                                                  spy={true}
                                                  smooth={true}
                                                  offset={-50}
                                                  duration={500}
                                                  onClick={toggle}
                                            >
                                                <span className={'menu-item'}>Contact</span>
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            )}
                        />
                        <div className={'menu-items menu-top'}>
                            <Link
                                activeClass="active"
                                to="about"
                                spy={true}
                                smooth={true}
                                offset={-50}
                                duration={500}
                            >
                                <span className={'menu-item'}>About Me</span>
                            </Link>
                            <Link
                                activeClass="active"
                                to="skills"
                                spy={true}
                                smooth={true}
                                offset={-50}
                                duration={500}
                            >
                                <span className={'menu-item'}>Skills</span>
                            </Link>
                            <Link
                                activeClass="active"
                                to="portfolio"
                                spy={true}
                                smooth={true}
                                offset={-50}
                                duration={500}
                            >
                                <span className={'menu-item'}>Portfolio</span>
                            </Link>
                            <Link activeClass="active"
                                  to="contact"
                                  spy={true}
                                  smooth={true}
                                  offset={-50}
                                  duration={500}
                            >
                                <span className={'menu-item'}>Contact</span>
                            </Link>
                        </div>
                    </div>
                </div>
            </nav>
        );
    }
}

export default Menu;
