import React, { Component } from 'react';
import './Menu.css';
import SlideToggle from 'react-slide-toggle';
import scrollToComponent from 'react-scroll-to-component';

class Menu extends Component {

	componentDidMount() {
		scrollToComponent(this.Violet, {offset: 0, align: 'middle', duration: 500, ease: 'inCirc'});
	}

	render() {
		return (
			<SlideToggle
				duration={800}
				collapsed
				render={({toggle, setCollapsibleElement, progress}) => (
					<div className="menu">
						<div className={'mobile-menu'}>
							<div className="slide-toggle__header">
								<div className="slide-toggle__button" onClick={toggle}>
									<div className={'line'}/>
									<div className={'line'}/>
									<div className={'line'}/>
								</div>
							</div>
							<div className="slide-toggle__box" ref={setCollapsibleElement}>
								<div className="slide-toggle__box-inner" style={{opacity: Math.max(0.5, progress)}}>
									<div className={'menu-items'} ref={'menu-items'}>
										<a className={'menu-item'} onClick={() => scrollToComponent(this.Violet, { offset: 0, align: 'top', duration: 1500})}>About</a>
										<a className={'menu-item'} href="index.html#skills" onClick={toggle}>Skills</a>
										<a className={'menu-item'} href="index.html#portfolio"
										   onClick={toggle}>Portfolio</a>
										<a className={'menu-item'} href="index.html#contact"
										   onClick={toggle}>Contact</a>
									</div>
								</div>
							</div>
						</div>

						<div className="desktop-menu">
							<div className={'menu-items menu-left'} ref={'menu-items'}>
								<a className={'menu-item'} onClick={() => scrollToComponent(this.Violet, { offset: 0, align: 'top', duration: 1500})}>About Me</a>
								<a className={'menu-item'} href="index.html#skills">Skills</a>
							</div>
							<div className={'menu-items menu-right'} ref={'menu-items'}>
								<a className={'menu-item'} href="index.html#portfolio">Portfolio</a>
								<a className={'menu-item'} href="index.html#contact">Contact</a>
							</div>
						</div>

					</div>
				)}
			/>
		);
	}
}
export default Menu;
