import React, {Component} from 'react';
import './Menu.css';
import {Link, animateScroll as scroll} from "react-scroll";


class Menu extends Component {

    scrollToTop = () => {
        scroll.scrollToTop();
    };

    render() {
        return (
            <nav className="nav" id="navbar">
                <div className="menu">
                    <div className="desktop-menu">
                        <Link
                            className={'logo'}
                            activeClass="active"
                            to="about"
                            spy={true}
                            smooth={true}
                            offset={-70}
                            duration={500}
                        >
                            <span>Nebojsa Mitrovic</span>
                        </Link>
                        <div className={'logo'}><span>Nebojsa Mitrovic</span></div>
                        <div className={'mobile-menu'}>
                            <div className="slide-toggle__header">
                                <div className="slide-toggle__button">
                                    <div className={'line'}/>
                                    <div className={'line'}/>
                                    <div className={'line'}/>
                                </div>
                            </div>
                            <div className="slide-toggle__box">
                                <div className="slide-toggle__box-inner">
                                    <div className={'menu-items mobile-menu-items'}>
                                        <a className={'menu-item'}>About</a>
                                        <a className={'menu-item'}>Skills</a>
                                        <a className={'menu-item'}>Portfolio</a>
                                        <a className={'menu-item'}>Contact</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className={'menu-items menu-top'}>
                            <Link
                                activeClass="active"
                                to="about"
                                spy={true}
                                smooth={true}
                                offset={-70}
                                duration={500}
                            >
                                <span className={'menu-item'}>About Me</span>
                            </Link>
                            <Link
                                activeClass="active"
                                to="skills"
                                spy={true}
                                smooth={true}
                                offset={-70}
                                duration={500}
                            >
                                <span className={'menu-item'}>Skills</span>
                            </Link>
                            <Link
                                activeClass="active"
                                to="portfolio"
                                spy={true}
                                smooth={true}
                                offset={-70}
                                duration={500}
                            >
                                <span className={'menu-item'}>Portfolio</span>
                            </Link>
                            <Link activeClass="active"
                                  to="contact"
                                  spy={true}
                                  smooth={true}
                                  offset={-70}
                                  duration={500}
                            >
                                <span className={'menu-item'}>Contact</span>
                            </Link>


                        </div>
                    </div>

                </div>
            </nav>
        );
    }
}

export default Menu;
